// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "./Governable.sol";

contract BounceProVoting is Configurable {
    using SafeMath for uint;
    using SafeERC20 for IERC20;

    bytes32 internal constant BotToken              = bytes32("BPV::BotToken");
    bytes32 internal constant CreationFee           = bytes32("BPV::CreationFee");
    bytes32 internal constant VotePassThreshold     = bytes32("BPV::VotePassThreshold");
    bytes32 internal constant MaxVotePeriod         = bytes32("BPV::MaxVotePeriod");
    address internal constant DeadAddress           = 0x000000000000000000000000000000000000dEaD;

    struct Pool {
        uint projectId;
        // address of pool creator
        address payable creator;
        // the timestamp in seconds the pool will be closed
        uint closeAt;
    }

    Pool[] public pools;

    // address => pool index => amount of votes
    mapping(address => mapping(uint => uint)) public myVotes;
    // address => pool index => true: claim, false: not claim
    mapping(address => mapping(uint => bool)) public myVotesClaimed;
    /// pool index => amount of total votes
    mapping(uint => uint) public totalVotes;
    // pool index => true: vote pass, false: vote not pass
    mapping(uint => bool) public votePassed;

    event Created(uint indexed index, address indexed sender, Pool pool);
    event Voted(uint indexed index, address indexed sender, uint amount);
    event Claimed(uint indexed index, address indexed sender, uint amount);
    event VoteHasPassed(uint indexed index);

    function initialize(address _governor) public override {
        require(msg.sender == governor || governor == address(0), "invalid governor");
        governor = _governor;

        config[CreationFee] = 0.3 ether;
        config[VotePassThreshold] = 300 ether;
        config[MaxVotePeriod] = 7 days;

        // mainnet
        config[BotToken] = uint(0x5bEaBAEBB3146685Dd74176f68a0721F91297D37);
    }

    function initialize_rinkeby(address _governor) public {
        require(msg.sender == governor || governor == address(0), "invalid governor");
        governor = _governor;

        config[CreationFee] = 0.3 ether;
        config[VotePassThreshold] = 300 ether;
        config[MaxVotePeriod] = 7 days;

        // rinkeby
        config[BotToken] = uint(0xAbF690E2EbC6690c4Fdc303fc3eE0FBFEb1818eD);
    }

    function create(uint projectId) public {
        IERC20(getBotToken()).safeTransferFrom(msg.sender, DeadAddress, getCreationFee());

        uint index = pools.length;
        Pool memory pool;
        pool.projectId = projectId;
        pool.creator = msg.sender;
        pool.closeAt = now.add(getMaxVotePeriod());
        pools.push(pool);

        emit Created(index, msg.sender, pool);
    }

    function vote(uint index, uint amount) external
        isPoolExist(index)
        isPoolNotClosed(index)
    {
        require(!votePassed[index], "vote passed");
        IERC20(getBotToken()).safeTransferFrom(msg.sender, address(this), amount);
        myVotes[msg.sender][index] = myVotes[msg.sender][index].add(amount);
        totalVotes[index] = totalVotes[index].add(amount);

        if (totalVotes[index] >= getVotePassThreshold()) {
            votePassed[index] = true;
            emit VoteHasPassed(index);
        }

        emit Voted(index, msg.sender, amount);
    }

    function claim(uint index) external
        isPoolExist(index)
        isPoolClosed(index)
    {
        require(!myVotesClaimed[msg.sender][index], "claimed");
        myVotesClaimed[msg.sender][index] = true;
        IERC20(getBotToken()).safeTransfer(msg.sender, myVotes[msg.sender][index]);
        emit Claimed(index, msg.sender, myVotes[msg.sender][index]);
    }

    function transferGovernor(address _governor) external {
        require(msg.sender == governor || governor == address(0), "invalid governor");
        governor = _governor;
    }

    function getPoolCount() public view returns (uint) {
        return pools.length;
    }

    function getBotToken() public view returns (address) {
        return address(config[BotToken]);
    }

    function getCreationFee() public view returns (uint) {
        return config[CreationFee];
    }

    function getVotePassThreshold() public view returns (uint) {
        return config[VotePassThreshold];
    }

    function getMaxVotePeriod() public view returns (uint) {
        return config[MaxVotePeriod];
    }

    modifier isPoolClosed(uint index) {
        require(pools[index].closeAt <= now || votePassed[index], "this pool is not closed");
        _;
    }

    modifier isPoolNotClosed(uint index) {
        require(pools[index].closeAt > now, "this pool is closed");
        _;
    }

    modifier isPoolExist(uint index) {
        require(index < pools.length, "this pool does not exist");
        _;
    }
}
